INSERT INTO `destoon_sell_data_5` VALUES('46','<p>\r\n	<br />\r\n</p>\r\n<p class=\"\\&quot;MsoNormal\\&quot;\" style=\"text-align:center;\">\r\n	<strong><span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:16px;\\\"=\"\">天津奥特泵业有限责任公司&lt;\\/span&gt;&lt;\\/strong&gt;<strong><span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:16px;\\\"=\"\">是一家集设计、开发、生产、销售、服务于一体的专业潜水泵制造商。&lt;\\/span&gt;&lt;\\/strong&gt;<strong><span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:18px;\\\"=\"\">&lt;\\/span&gt;&lt;\\/strong&gt; &lt;\\/p&gt;</span></strong></span></strong></span></strong>\r\n</p>\r\n<p class=\"\\&quot;MsoNormal\\&quot;\" style=\"text-align:left;\">\r\n	<strong><strong><strong><span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:18px;\\\"=\"\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&lt;\\/span&gt;<span style=\"font-size:16px;\">本公司根据用户需要生产海水、工厂腐蚀性液体等特殊场合用潜水电泵。该泵主机由QJH型潜水泵和YQSH型潜水电机组成。&lt;\\/span&gt;<span style=\"font-size:16px;\">潜入水下工作，具有结构紧凑、体积小、重量轻，安装、使用、维护方便，运输安全可靠、节约能源等优点。&lt;\\/span&gt;&lt;\\/span&gt;&lt;\\/p&gt;</span></span></span></span></strong></strong></strong>\r\n</p>\r\n<p class=\"\\&quot;MsoNormal\\&quot;\" style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><strong><strong><span style=\"line-height:normal;\"><span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:18px;color:#4c33e5;\\\"=\"\"><strong><span style=\"color:#000000;font-size:16px;\">津奥特潜水泵产品执行标准&lt;\\/span&gt;<span style=\"color:#000000;font-size:16px;\">：&lt;\\/span&gt;&lt;\\/strong&gt;&lt;\\/span&gt;<br />\r\n<span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:16px;\\\"=\"\">1 国家标准GB/T2816-2002《井用潜水泵》。&lt;\\/span&gt;<br />\r\n<span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:16px;\\\"=\"\">2 国家标准GB/T12785-2002《潜水电泵 试验方法》。&lt;\\/span&gt;<br />\r\n<span style=\"\\&quot;font-family:\'Microsoft\" yahei\';font-size:16px;\\\"=\"\">3 本公司Q/TAT4609-2006 《Q（H）系列潜（海）水泵》标准。&lt;\\/span&g